          image: 'https://images.unsplash.com/photo-1611162617213-7d7a39e9b1d7?w=400&h=250&fit=crop',
          isEnrolled: false
        },
        {
          _id: '5',
          title: 'YouTube Ads Masterclass',
          description: 'Pelajari cara membuat dan mengoptimalkan iklan YouTube yang menarik dan menghasilkan ROI tinggi.',
          category: 'Digital Marketing',
          instructor: 'Rina Video',
          duration: '7 jam',
          lessons: 7,
          students: 178,
          rating: 4.5,
          price: 349000,
          image: 'https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=400&h=250&fit=crop',
          isEnrolled: false
        },
        {
          _id: '6',
          title: 'LinkedIn Business Growth',
          description: 'Strategi LinkedIn untuk B2B marketing dan networking yang efektif untuk pertumbuhan bisnis.',
          category: 'Business',
          instructor: 'Michael Business',
          duration: '4 jam',
          lessons: 4,
          students: 95,
          rating: 4.8,
          price: 199000,
          image: 'https://images.unsplash.com/photo-1611162617213-7d7a39e9b1d7?w=400&h=250&fit=crop',
          isEnrolled: false
        },
        {
          _id: '7',
          title: 'Facebook Shop Setup',
          description: 'Panduan lengkap setup Facebook Shop dan Instagram Shopping untuk e-commerce.',
          category: 'Social Media',
          instructor: 'Lisa Ecommerce',
          duration: '3 jam',
          lessons: 3,
          students: 267,
          rating: 4.6,
          price: 149000,
          image: 'https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=400&h=250&fit=crop',
          isEnrolled: false
        },
        {
          _id: '8',
          title: 'Digital Marketing Fundamentals',
          description: 'Dasar-dasar digital marketing untuk pemula yang ingin memulai karir di dunia marketing digital.',
          category: 'Digital Marketing',
          instructor: 'Coach Digital',
          duration: '12 jam',
          lessons: 12,
          students: 445,
          rating: 4.9,
          price: 499000,
          image: 'https://images.unsplash.com/photo-1611162617213-7d7a39e9b1d7?w=400&h=250&fit=crop',
          isEnrolled: false
        }
      ]);
    } catch (error) {
      console.error('Error fetching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEnroll = async (courseId) => {
    try {
      // TODO: Implement enrollment API
      // await courseAPI.enroll(courseId);
      console.log('Enrolling in course:', courseId);
    } catch (error) {
      console.error('Error enrolling:', error);
    }
  };

  const handleDelete = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      try {
        // TODO: Implement delete API
        // await courseAPI.delete(courseId);
        setCourses(courses.filter(course => course._id !== courseId));
        console.log('Course deleted:', courseId);
      } catch (error) {
        console.error('Error deleting course:', error);
      }
    }
  };

  const filteredCourses = courses.filter(course => {
    const matchesSearch = course.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         course.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = filterCategory === 'all' || course.category === filterCategory;
    return matchesSearch && matchesCategory;
  });

  const categories = ['all', 'Digital Marketing', 'Social Media', 'Business', 'Technology'];

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-white">Courses</h1>
              <p className="text-gray-400 mt-2">Discover and learn from our expert-led courses</p>
            </div>
            {hasRole('admin') && (
              <Link
                to="/create-course"
                className="flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
              >
                <Plus className="h-5 w-5 mr-2" />
                Create Course
              </Link>
            )}
          </div>
        </div>

        {/* Search and Filter */}
        <div className="mb-8 flex flex-col sm:flex-row gap-4">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search courses..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 bg-gray-800 border border-gray-700 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="flex items-center gap-2">
            <Filter className="h-5 w-5 text-gray-400" />
            <select
              value={filterCategory}
              onChange={(e) => setFilterCategory(e.target.value)}
              className="px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              {categories.map(category => (
                <option key={category} value={category}>
                  {category === 'all' ? 'All Categories' : category}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Courses Grid */}
        {filteredCourses.length === 0 ? (
          <div className="text-center py-12">
            <BookOpen className="h-16 w-16 text-gray-600 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-gray-300 mb-2">No courses found</h3>
            <p className="text-gray-500">Try adjusting your search or filter criteria</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredCourses.map((course) => (
              <div key={course._id} className="bg-gray-800 rounded-xl border border-gray-700 overflow-hidden hover:border-gray-600 transition-colors">
                {/* Course Image */}
                <div className="relative h-48 bg-gray-700">
                  <img
                    src={course.image}
                    alt={course.title}
                    className="w-full h-full object-cover"
                  />
                  <div className="absolute top-3 right-3">
                    <span className="px-2 py-1 bg-blue-600 text-white text-xs rounded-full">
                      {course.category}
                    </span>
                  </div>
                </div>

                {/* Course Content */}
                <div className="p-6">
                  <h3 className="text-xl font-semibold text-white mb-2 line-clamp-2">
                    {course.title}
                  </h3>
                  <p className="text-gray-400 text-sm mb-4 line-clamp-3">
                    {course.description}
                  </p>

                  {/* Course Meta */}
                  <div className="flex items-center justify-between text-sm text-gray-400 mb-4">
                    <div className="flex items-center">
                      <Clock className="h-4 w-4 mr-1" />
                      {course.duration}
                    </div>
                    <div className="flex items-center">
                      <BookOpen className="h-4 w-4 mr-1" />
                      {course.lessons} lessons
                    </div>
                    <div className="flex items-center">
                      <Users className="h-4 w-4 mr-1" />
                      {course.students}
                    </div>
                  </div>

                  {/* Rating */}
                  <div className="flex items-center mb-4">
                    <div className="flex items-center">
                      {[...Array(5)].map((_, i) => (
                        <Star
                          key={i}
                          className={`h-4 w-4 ${
                            i < Math.floor(course.rating)
                              ? 'text-yellow-400 fill-current'
                              : 'text-gray-600'
                          }`}
                        />
                      ))}
                    </div>
                    <span className="text-gray-400 text-sm ml-2">
                      {course.rating} ({course.students} students)
                    </span>
                  </div>

                  {/* Price and Actions */}
                  <div className="flex items-center justify-between">
                    <div className="text-2xl font-bold text-white">
                      Rp {course.price.toLocaleString()}
                    </div>
                    <div className="flex items-center gap-2">
                      {hasRole('admin') ? (
                        <>
                          <Link
                            to={`/edit-course/${course._id}`}
                            className="p-2 text-blue-400 hover:text-blue-300 transition-colors"
                          >
                            <Edit className="h-5 w-5" />
                          </Link>
                          <button
                            onClick={() => handleDelete(course._id)}
                            className="p-2 text-red-400 hover:text-red-300 transition-colors"
                          >
                            <Trash2 className="h-5 w-5" />
                          </button>
                        </>
                      ) : (
                        <>
                          <Link
                            to={`/courses/${course._id}`}
                            className="p-2 text-blue-400 hover:text-blue-300 transition-colors"
                          >
                            <Eye className="h-5 w-5" />
                          </Link>
                          {course.isEnrolled ? (
                            <Link
                              to={`/courses/${course._id}`}
                              className="flex items-center px-3 py-1 bg-green-600 hover:bg-green-700 text-white text-sm rounded-lg transition-colors"
                            >
                              <Play className="h-4 w-4 mr-1" />
                              Continue
                            </Link>
                          ) : (
                            <button
                              onClick={() => handleEnroll(course._id)}
                              className="flex items-center px-3 py-1 bg-blue-600 hover:bg-blue-700 text-white text-sm rounded-lg transition-colors"
                            >
                              Enroll
                            </button>
                          )}
                        </>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
